for(var link=document.querySelectorAll(".goods-item .btn, .goods-item-buy"),popup=document.querySelector(".modal-content-order"),close=document.querySelectorAll(".modal-close"),link2=document.querySelector(".info-contacts .btn"),popup2=document.querySelector(".modal-content"),i=0;i<link.length;i++)link[i].addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-content-show")});for(var i=0;i<close.length;i++)close[i].addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-content-show"),popup2.classList.remove("modal-content-show")});link2.addEventListener("click",function(e){e.preventDefault(),popup2.classList.add("modal-content-show")});